package org.baichuan.sample.algorithms.leetcode.simple

import java.util.*

class IsValid {
    fun isValid(s: String): Boolean {
        if (s.length % 2 != 0) {
            return false
        }
        val stack: Stack<Char> = Stack()
        for (c in s.toCharArray()) {
            when (c) {
                ')' -> if (stack.isEmpty() || stack.pop() != '(') return false
                ']' -> if (stack.isEmpty() || stack.pop() != '[') return false
                '}' -> if (stack.isEmpty() || stack.pop() != '{') return false
                else -> stack.push(c)
            }
        }
        return stack.isEmpty()
    }
}

